flowFile = session.get();

if (flowFile != null) {
    var File = Java.type("java.io.File");
    var path = flowFile.getAttribute("path");

    var f = new File(path);

    if (! f.exists() ) {
        session.putAttribute(flowFile,"path.type","new");
    } 
    else if (f.isDirectory()) {
    	session.putAttribute(flowFile,"path.type","directory");
    }
    else if (f.isFile()) {
        session.putAttribute(flowFile,"path.type","file");
    }
    else {
        session.putAttribute(flowFile,"path.type","other");
    }

    session.transfer(flowFile, REL_SUCCESS);
}

